CREATE OR REPLACE PROCEDURE pls_sip_pck.sip_grava_qt_evento_pct ( tb_nr_seq_p INOUT pls_util_cta_pck.t_number_table, tb_qt_proc_mat_p INOUT pls_util_cta_pck.t_number_table, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	grava a contagem de eventos para pacotes

	Apenas faz o forall, nao deve ser incluso nenhuma regra de selecao ou negocio aqui
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

forall i in tb_nr_seq_p.first..tb_nr_seq_p.last
	update	sip_nv_dados
	set	qt_proc_mat	= tb_qt_proc_mat_p(i)
	where	nr_sequencia	= tb_nr_seq_p(i);
	
if (coalesce(ie_commit_p, 'N') = 'S') then
	
	commit;
end if;

if (coalesce(ie_esvaziar_table_p, 'S') = 'S') then

	tb_nr_seq_p.delete;
	tb_qt_proc_mat_p.delete;
end if;
	
END;

$body$
