SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_sip_pck.sip_nv_alimenta_qt_vidas ( nr_seq_lote_p pls_lote_sip.nr_sequencia%type) AS $body$
DECLARE


dt_base_null_w	timestamp := to_date('1500-01-01 00:00:00', 'yyyy-mm-dd hh24:mi:ss');

BEGIN
	-- dt_contratacao is not null e dt_rescisao is null

	update 	pls_lote_sip
	set	qt_vidas_operadora =	(	SELECT	count(1)
						from   	pls_segurado    a
						where   a.ie_tipo_segurado in ('B','R')
						and	coalesce(a.dt_contratacao, dt_base_null_w) != dt_base_null_w
						and	coalesce(a.dt_rescisao, dt_base_null_w) = dt_base_null_w)
	where	nr_sequencia	= nr_seq_lote_p;
	commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
