SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_sip_pck.sip_nv_retorna_uf ( ie_tipo_protocolo_p pls_protocolo_conta.ie_tipo_protocolo%type, nr_seq_congenere_p pls_protocolo_conta.nr_seq_congenere%type, nr_seq_prestador_exec_p pls_conta.nr_seq_prestador_exec%type, nr_seq_conta_p pls_conta.nr_sequencia%type, cd_pf_conta_p pls_conta.cd_pessoa_fisica%type, cd_cgc_conta_p pls_conta.cd_cgc%type) RETURNS SIP_NV_DADOS.SG_UF%TYPE AS $body$
DECLARE


retorno_w	sip_nv_dados.sg_uf%type;


BEGIN
-- se for um protocolo de intercambio ira buscar o estado da congenere

if (ie_tipo_protocolo_p in ('I','F')) then

	-- Busca o estado de endereco cadastrado para a congenere conforme sugerido por Roni Bento e acatado

	-- com aval do analista de negocios da Philips.

	select	max(b.sg_estado)
	into STRICT	retorno_w
	from	pls_congenere		a,
		pessoa_juridica		b
	where	a.nr_sequencia 	= nr_seq_congenere_p
	and	b.cd_cgc	= a.cd_cgc;
	
-- se for um protocolo de Reembolso, ira buscar o estado do Beneficiario ou empresa da conta.

elsif (ie_tipo_protocolo_p = 'R') then

	retorno_w := pls_sip_pck.obter_uf_reembolso(nr_seq_conta_p, cd_pf_conta_p, cd_cgc_conta_p);
else
	select	max(sg_uf_sip)
	into STRICT	retorno_w
	from	pls_prestador
	where	nr_sequencia	= nr_seq_prestador_exec_p;
end if;

-- se nao teve informacao marca como NC  - Nacional.

if (coalesce(retorno_w::text, '') = '') then

	retorno_w	:= 'NC';	
end if;

return retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
