CREATE OR REPLACE PROCEDURE pls_smp_pck.atualiza_valores_prest ( regra_simulacao_p pls_smp_pck.regra_simulacao) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Atualizar o valor de simulacao de todos os beneficiarios e prestadores
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
-- Carrega os beneficiarios
c01 CURSOR(	nr_seq_smp_pc	regra_simulacao_p.nr_sequencia%type) FOR
SELECT	nr_sequencia
from	pls_smp_result_benef
where	nr_seq_smp	= nr_seq_smp_pc;

BEGIN

-- so executa se tiver regras
if (regra_simulacao_p.nr_sequencia IS NOT NULL AND regra_simulacao_p.nr_sequencia::text <> '') then
	
	-- navega pelos beneficiarios
	for r_c01_w in c01(regra_simulacao_p.nr_sequencia) loop
		CALL pls_smp_pck.atualiza_valores_prest(r_c01_w.nr_sequencia, null);
	end loop; -- Fim c01 beneficiarios
end if; -- fim existe regra
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
