CREATE OR REPLACE PROCEDURE pls_smp_pck.gerar_prestador ( regra_simulacao_p pls_smp_pck.regra_simulacao, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Gerar os dados de prestador
	
	Prestadores: Para cada beneficiario, sera replicado todos os prestadores da
	simulacao, fazendo a ligacao com o campo nr_seq_smp_result_benef.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
-- beneficiarios da regra
c01 CURSOR(	nr_seq_smp_pc	pls_smp.nr_sequencia%type) FOR
SELECT	nr_sequencia
from	pls_smp_result_benef
where	nr_seq_smp	= nr_seq_smp_pc;

BEGIN

if (regra_simulacao_p.nr_sequencia IS NOT NULL AND regra_simulacao_p.nr_sequencia::text <> '') then

	for r_c01_w in c01(regra_simulacao_p.nr_sequencia) loop
		
		insert into pls_smp_result_prest(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_smp,
							nr_seq_smp_result_benef,
							nr_seq_prestador,
							vl_simulacao_prest)
		SELECT	nextval('pls_smp_result_prest_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			regra_simulacao_p.nr_sequencia,
			r_c01_w.nr_sequencia,
			nr_seq_prestador,
			0
		from	pls_smp_prest
		where	nr_seq_smp	= regra_simulacao_p.nr_sequencia;
		
		commit;
						
	end loop; -- c01 - Beneficiarios
end if; -- fim regra simulacao existe
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
