SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_smp_pck.utilizar_menor_valor_regra ( regra_simulacao_p pls_smp_pck.regra_simulacao) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Define para cada item o menor preco localizado.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
c01 CURSOR(nr_seq_smp_pc	pls_smp_result_prest.nr_seq_smp%type) FOR
SELECT	nr_sequencia
from	pls_smp_result_prest
where	nr_seq_smp	= nr_seq_smp_pc;
BEGIN

-- Apenas se tiver regra informada
if (regra_simulacao_p.nr_sequencia IS NOT NULL AND regra_simulacao_p.nr_sequencia::text <> '') then
	
	-- percorre os prestadores da simulacao
	for r_c01_w in c01(regra_simulacao_p.nr_sequencia) loop
		update	pls_smp_result_item	a
		set	a.vl_simulacao	= (SELECT coalesce(min(vl_simulacao),0) * coalesce(a.qt_item,0) from pls_smp_result_regra where nr_seq_smp_result_regra = a.nr_sequencia)
		where	a.nr_seq_smp_result_prest	= r_c01_w.nr_sequencia
		and	a.ie_tipo_vinculacao		= 'A';
	
		commit;
	end loop; -- percorre os prestadores
end if; -- fim regra existe
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
