SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_smp_pck.valida_simulacao ( regra_simulacao_p pls_smp_pck.regra_simulacao) AS $body$
DECLARE


qt_registro_w		integer;

BEGIN

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Realizar a validacao dos dados minimos necessario para a simulacao
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
-- deve existir pelo menos 1 beneficiario
select	count(1)
into STRICT	qt_registro_w
from	pls_smp_benef
where	nr_seq_smp	= regra_simulacao_p.nr_sequencia;

if (qt_registro_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(408936); -- e necessario um beneficiario
end if;

-- deve existir pelo menos 1 prestador
select	count(1)
into STRICT	qt_registro_w
from	pls_smp_prest
where	nr_seq_smp	= regra_simulacao_p.nr_sequencia;

if (qt_registro_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(408958); -- e necessario um prestador
end if;


-- deve existir pelo menos 1 procedimento ou material
select	sum(t.contador)
into STRICT	qt_registro_w
from (	SELECT	count(1) contador
		from	pls_smp_proc
		where	nr_seq_smp	= regra_simulacao_p.nr_sequencia
		
union all

		SELECT	count(1) contador
		from	pls_smp_mat
		where	nr_seq_smp	= regra_simulacao_p.nr_sequencia
	) t;

if (qt_registro_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(408959); -- e necessario um procedimento ou material
end if;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
