SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_solic_rescisao_fin_pck.baixa_titulo_nota_credito ( nr_titulo_p titulo_receber.nr_titulo%type, nr_seq_solic_resc_fin_p pls_solic_resc_fin_hist.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ds_saldo_retorno_p INOUT text) AS $body$
DECLARE


vl_baixa_titulo_w		double precision;
nr_seq_nota_credito_w		nota_credito.nr_sequencia%type;
vl_saldo_nota_credito_w		nota_credito.vl_saldo%type;
nr_seq_baixa_w			titulo_receber_liq.nr_sequencia%type;
cd_moeda_padrao_w		parametro_contas_receber.cd_moeda_padrao%type;
nr_titulo_contab_w		bigint;
nr_seq_regra_w			pls_resc_fin_mens.nr_sequencia%type;
cd_tipo_recebimento_nc_w	pls_resc_fin_mens.cd_tipo_recebimento_nc%type;
nr_seq_trans_fin_w		tipo_recebimento.nr_seq_trans_fin%type;
ie_tipo_titulo_w		titulo_receber.ie_tipo_titulo%type;


BEGIN

begin
select	cd_moeda_padrao
into STRICT	cd_moeda_padrao_w
from	parametro_contas_receber
where	cd_estabelecimento = cd_estabelecimento_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(176708);
end;

nr_seq_regra_w	:= pls_obter_regra_resc_fin(nr_seq_solic_resc_fin_p, cd_estabelecimento_p);

select	max(cd_tipo_recebimento_nc)
into STRICT	cd_tipo_recebimento_nc_w
from	pls_resc_fin_mens
where	nr_sequencia	= nr_seq_regra_w;

if (coalesce(cd_tipo_recebimento_nc_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(838362); --Favor realizar o cadastro do tipo de baixa padrao na aba Regras / Financeiro.
end if;

select	max(ie_tipo_titulo)
into STRICT	ie_tipo_titulo_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

select	max(nr_seq_trans_fin)
into STRICT	nr_seq_trans_fin_w
from	tipo_recebimento
where	cd_tipo_recebimento = cd_tipo_recebimento_nc_w;

vl_baixa_titulo_w		:= (pls_solic_rescisao_fin_pck.obter_saldo_baixar_titulo(nr_titulo_p, nr_seq_solic_resc_fin_p, 'V'))::numeric;

nr_seq_nota_credito_w		:= pls_solic_rescisao_fin_pck.obter_nr_seq_nota_credito(nr_seq_solic_resc_fin_p);

insert	into titulo_receber_nc(dt_atualizacao,
	nm_usuario,
	nr_seq_nota_credito,
	nr_sequencia,
	nr_titulo_rec,
	vl_nota_credito)
values (clock_timestamp(),
	nm_usuario_p,
	nr_seq_nota_credito_w,
	nextval('titulo_receber_nc_seq'),
	nr_titulo_p,
	vl_baixa_titulo_w);
	
select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_seq_baixa_w
from	titulo_receber_liq
where	nr_titulo	= nr_titulo_p;

insert into titulo_receber_liq(nr_sequencia, cd_moeda, cd_tipo_recebimento,
	dt_atualizacao, dt_recebimento, ie_acao,
	ie_lib_caixa, nm_usuario, nr_titulo,
	vl_descontos, vl_glosa, vl_juros,
	vl_multa, vl_recebido, vl_rec_maior,
	vl_nota_credito, ds_observacao, nr_seq_trans_fin,
	dt_real_recebimento, vl_adequado, vl_despesa_bancaria,
	vl_perdas, vl_outros_acrescimos, vl_cambial_ativo,
	vl_cambial_passivo)
values (nr_seq_baixa_w, cd_moeda_padrao_w, cd_tipo_recebimento_nc_w,
	clock_timestamp(), clock_timestamp(), 'I',
	'S', nm_usuario_p, nr_titulo_p,
	0, 0, 0,
	0, 0, 0,
	vl_baixa_titulo_w, wheb_mensagem_pck.get_texto(1107905, 'NR_SEQ_NOTA_CREDITO='||nr_seq_nota_credito_w), nr_seq_trans_fin_w,
	clock_timestamp(), 0, 0,
	0, 0, 0,
	0);
	
insert into nota_credito_baixa(nr_sequencia, dt_atualizacao, nm_usuario,
	dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_nota_credito,
	vl_baixa, nr_titulo, dt_baixa)
values (nextval('nota_credito_baixa_seq'), clock_timestamp(), nm_usuario_p,
	clock_timestamp(), nm_usuario_p, nr_seq_nota_credito_w,
	vl_baixa_titulo_w, nr_titulo_p, clock_timestamp());
	
update	titulo_receber_nc
set	nr_seq_liq = nr_seq_baixa_w
where	nr_titulo_rec = nr_titulo_p
and	nr_seq_nota_credito = nr_seq_nota_credito_w;
	
CALL atualizar_saldo_nota_credito(nr_seq_nota_credito_w, nm_usuario_p);
CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_p, null, nm_usuario_p, nr_titulo_p, nr_seq_baixa_w);
pls_gerar_tit_rec_liq_mens(nr_titulo_p, nr_seq_baixa_w, nm_usuario_p, nr_titulo_contab_w);
CALL pls_gerar_amortizacao_regra(nr_titulo_p, nr_seq_baixa_w, nm_usuario_p, 'N');
CALL atualizar_saldo_tit_rec(nr_titulo_p, nm_usuario_p);

if (ie_tipo_titulo_w <> '10') then
	CALL gerar_bloqueto_tit_rec(nr_titulo_p,'OPSMS'); --Atualizar a linha digitavel
end if;

vl_saldo_nota_credito_w		:= pls_solic_rescisao_fin_pck.obter_saldo_nota_credito(nr_seq_nota_credito_w);

ds_saldo_retorno_p		:= wheb_mensagem_pck.get_texto(1041127, 'VL_SALDO_NC='||vl_saldo_nota_credito_w);

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_solic_rescisao_fin_pck.baixa_titulo_nota_credito ( nr_titulo_p titulo_receber.nr_titulo%type, nr_seq_solic_resc_fin_p pls_solic_resc_fin_hist.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ds_saldo_retorno_p INOUT text) FROM PUBLIC;
