CREATE OR REPLACE PROCEDURE pls_solic_rescisao_fin_pck.cancelar_analise ( nr_seq_solic_resc_fin_p pls_solic_rescisao_fin.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_commit_p text) AS $body$
DECLARE

ie_status_w			pls_solic_rescisao_fin.ie_status%type;

BEGIN
if (nr_seq_solic_resc_fin_p IS NOT NULL AND nr_seq_solic_resc_fin_p::text <> '') then
	select	ie_status
	into STRICT	ie_status_w
	from	pls_solic_rescisao_fin
	where	nr_sequencia	= nr_seq_solic_resc_fin_p;
	
	if (ie_status_w = 1) then
		CALL pls_solic_rescisao_fin_pck.gravar_analise_financeira(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p);
	end if;
	
	update	pls_solic_rescisao_fin
	set	ie_status		= 7,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_solic_resc_fin_p;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
