CREATE OR REPLACE PROCEDURE pls_solic_rescisao_fin_pck.gerar_nova_analise ( nr_seq_solic_resc_fin_p pls_solic_rescisao_fin.nr_sequencia%type, nr_seq_solicitacao_p pls_solicitacao_rescisao.nr_sequencia%type, nr_seq_pagador_p pls_contrato_pagador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				
qt_analise_pagador_w	integer;
				

BEGIN

select	count(1)
into STRICT	qt_analise_pagador_w
from	pls_solic_rescisao_fin
where	nr_seq_solicitacao = nr_seq_solicitacao_p
and	nr_seq_pagador = nr_seq_pagador_p
and	ie_status not in ('7','8');

if (qt_analise_pagador_w = 0) then
	insert into pls_solic_rescisao_fin(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
		nm_usuario, nm_usuario_nrec, ie_status,
		nr_seq_pagador, nr_seq_solicitacao)
	SELECT	nextval('pls_solic_rescisao_fin_seq'), clock_timestamp(), clock_timestamp(),
		nm_usuario_p, nm_usuario_p, '1',
		nr_seq_pagador, nr_seq_solicitacao
	from	pls_solic_rescisao_fin
	where	nr_sequencia = nr_seq_solic_resc_fin_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1128593);
end if;

commit;

END;

$body$
