CREATE OR REPLACE FUNCTION pls_solic_rescisao_fin_pck.obter_lista_beneficiarios ( nr_seq_solic_resc_fin_p pls_solic_rescisao_fin.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_tipo_devolucao_p text) RETURNS text AS $body$
DECLARE

ds_retorno_w		text;

C01 CURSOR FOR
	SELECT	b.dt_referencia,
		substr(pls_obter_dados_segurado(a.nr_seq_segurado,'N'),1,255) nm_pessoa_fisica,
		substr(pls_obter_dados_segurado(a.nr_seq_segurado,'C'),1,255) cd_usuario_plano,
		sum(a.vl_devolver) vl_devolver,
		a.nr_titulo nr_titulo
	from	table(pls_obj_dados_rescisao_pck.obter_dados_rescisao(nr_seq_solic_resc_fin_p,cd_estabelecimento_p)) a,
		pls_mensalidade b
	where	b.nr_sequencia	= a.nr_seq_mensalidade
	and	a.ie_tipo_item = 1
	and	a.vl_devolver <> 0
	and	ie_tipo_devolucao_p = 'NC'
	group by a.nr_seq_segurado, b.dt_referencia, a.nr_titulo
	
union all

	SELECT	b.dt_referencia,
		substr(pls_obter_dados_segurado(a.nr_seq_segurado,'N'),1,255) nm_pessoa_fisica,
		substr(pls_obter_dados_segurado(a.nr_seq_segurado,'C'),1,255) cd_usuario_plano,
		sum(a.vl_cancelar) vl_devolver,
		a.nr_titulo nr_titulo
	from	table(pls_obj_dados_rescisao_pck.obter_dados_rescisao(nr_seq_solic_resc_fin_p,cd_estabelecimento_p)) a,
		pls_mensalidade b
	where	b.nr_sequencia	= a.nr_seq_mensalidade
	and	a.ie_tipo_item = 1
	and	a.vl_cancelar <> 0
	and	ie_tipo_devolucao_p = 'A'
	group by a.nr_seq_segurado, b.dt_referencia, a.nr_titulo
	order by 1,2;
BEGIN
ds_retorno_w	:= null;

for r_c01_w in c01 loop
	begin
	ds_retorno_w	:= ds_retorno_w||wheb_mensagem_pck.get_texto(1107893, 	'DS_RETORNO='||''||';'||
										'NM_PESSOA_FISICA='||r_c01_w.nm_pessoa_fisica||';'||
										'CD_USUARIO_PLANO='||r_c01_w.cd_usuario_plano||';'||
										'DT_REFERENCIA='||to_char(r_c01_w.dt_referencia,'mm/yyyy')||';'||
										'VL_DEVOLVER='||to_char(r_c01_w.vl_devolver, 'FM999G999G990D90'));

	if (ie_tipo_devolucao_p = 'A') then
		ds_retorno_w := ds_retorno_w || ' (' || r_c01_w.nr_titulo || ')';
	end if;

	ds_retorno_w := ds_retorno_w || chr(13);
	end;
end loop;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
