SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_solic_rescisao_fin_pck.obter_saldo_baixar_titulo ( nr_titulo_p titulo_receber.nr_titulo%type, nr_seq_solic_resc_fin_p pls_solic_resc_fin_hist.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
M	Mensagem
V	Valor
*/


ds_retorno_w		varchar(4000);
vl_saldo_titulo_w	titulo_receber.vl_saldo_titulo%type;
nr_seq_nota_credito_w	nota_credito.nr_sequencia%type;
vl_saldo_nota_credito_w	nota_credito.vl_saldo%type;
vl_baixa_tit_w		double precision;


BEGIN

select	coalesce(vl_saldo_titulo,0)
into STRICT	vl_saldo_titulo_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

nr_seq_nota_credito_w	:= pls_solic_rescisao_fin_pck.obter_nr_seq_nota_credito(nr_seq_solic_resc_fin_p);

vl_saldo_nota_credito_w	:= pls_solic_rescisao_fin_pck.obter_saldo_nota_credito(nr_seq_nota_credito_w);

if (vl_saldo_nota_credito_w >= vl_saldo_titulo_w) then
	vl_baixa_tit_w	:= vl_saldo_titulo_w;
else
	vl_baixa_tit_w	:= vl_saldo_nota_credito_w;
end if;

if (ie_opcao_p = 'M') then
	ds_retorno_w	:= substr(wheb_mensagem_pck.get_texto(1041123, 'VL_BAIXA='||vl_baixa_tit_w||';'||'NR_TITULO='||nr_titulo_p),1,4000);
elsif (ie_opcao_p = 'V') then
	ds_retorno_w	:= substr(vl_baixa_tit_w,1,4000);
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
