CREATE OR REPLACE FUNCTION pls_solic_rescisao_fin_pck.obter_saldo_nota_credito ( nr_seq_nota_credito_p nota_credito.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


vl_saldo_nota_credito_w		nota_credito.vl_saldo%type;


BEGIN

select	coalesce(max(vl_saldo), 0)
into STRICT	vl_saldo_nota_credito_w
from	nota_credito
where	nr_sequencia = nr_seq_nota_credito_p;

return vl_saldo_nota_credito_w;

END;

$body$
