CREATE OR REPLACE PROCEDURE pls_store_data_mens_pck.store_pls_regra_mens_contr_vm ( dt_mesano_referencia_p timestamp) AS $body$
BEGIN
	 
	select	max(nr_sequencia) 
	into STRICT	current_setting('pls_store_data_mens_pck.nr_seq_regra_contr_vm_pf_w')::pls_regra_mens_contrato.nr_sequencia%type 
	from	pls_regra_mens_contrato 
	where	coalesce(nr_seq_contrato::text, '') = '' 
	and	coalesce(nr_seq_intercambio::text, '') = '' 
	and	ie_tipo_regra	= 'VM' 
	and	(vl_minimo_abat_fatura IS NOT NULL AND vl_minimo_abat_fatura::text <> '') 
	and	((coalesce(ie_pessoa_contrato,'A') = 'PF') or (ie_pessoa_contrato = 'A')) 
	and	trunc(dt_mesano_referencia_p,'dd') between coalesce(dt_inicio_vigencia,dt_mesano_referencia_p) and coalesce(dt_fim_vigencia,dt_mesano_referencia_p);
	 
	select	max(nr_sequencia) 
	into STRICT	current_setting('pls_store_data_mens_pck.nr_seq_regra_contr_vm_pj_w')::pls_regra_mens_contrato.nr_sequencia%type 
	from	pls_regra_mens_contrato 
	where	coalesce(nr_seq_contrato::text, '') = '' 
	and	coalesce(nr_seq_intercambio::text, '') = '' 
	and	ie_tipo_regra	= 'VM' 
	and	(vl_minimo_abat_fatura IS NOT NULL AND vl_minimo_abat_fatura::text <> '') 
	and	((coalesce(ie_pessoa_contrato,'A') = 'PJ') or (ie_pessoa_contrato = 'A')) 
	and	trunc(dt_mesano_referencia_p,'dd') between coalesce(dt_inicio_vigencia,dt_mesano_referencia_p) and coalesce(dt_fim_vigencia,dt_mesano_referencia_p);
	 
	end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
