--Atualiza regra de intercambio e taxa de  uma quantidade especifica de procedimentos ou materiais



CREATE OR REPLACE PROCEDURE pls_taxa_intercambio_pck.atualiza_intercambio_item ( tb_seq_item_p dbms_sql.number_table, tb_seq_regra_p dbms_sql.number_table, tb_pr_regra_p dbms_sql.number_table, ie_tipo_item_p text) AS $body$
BEGIN
--Se for tabela de procedimentos

if (ie_tipo_item_p = 'P') then
	if (tb_seq_item_p.count > 0 ) then
		--Faz update dos registros(delimitados)

		forall i in tb_seq_item_p.first..tb_seq_item_p.last			
			update	pls_conta_proc
			set	
				nr_seq_regra_tx_inter	= tb_seq_regra_p(i),
				tx_intercambio		= tb_pr_regra_p(i)
			where	nr_sequencia		= tb_seq_item_p(i);			
			commit;
	end if;
else
	if (tb_seq_item_p.count > 0 ) then
		--Faz update dos registros(delimitados)		

		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	pls_conta_mat
			set	
				nr_seq_regra_tx_inter	= tb_seq_regra_p(i),
				tx_intercambio		= tb_pr_regra_p(i)
			where	nr_sequencia		= tb_seq_item_p(i);
			commit;
	end if;
end if;

END;

$body$
