CREATE OR REPLACE PROCEDURE pls_taxa_intercambio_pck.pls_gerencia_tx_inter ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_mat_p pls_conta_mat.nr_sequencia%type, ie_tipo_tx_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type ) AS $body$
DECLARE

qt_conta_lote_w	integer;

BEGIN

/*
---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

**************************************************************************************************************************************************************************************
**************************************************************************************************************************************************************************************
**************************************************************************************************************************************************************************************
********* SE FOR ALTERAR ALGUMA COISA NESTA ROTINA, FAVOR VERIFICAR A PLS_CTA_VALORIZACAO_PCK A FUNCTION OBTER_TAXA_INTERCAMBIO**************************************
********* HOUVE DUPLICACAO DE CODIGO PARA MANTERMOS AS TAXAS DE fINTERCAMBIO FUNCIONANDO NOS DOIS MODELOS ************************************************************
**************************************************************************************************************************************************************************************
**************************************************************************************************************************************************************************************
**************************************************************************************************************************************************************************************
---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

*/


-- se for por processo por lote alimenta tabela temporaria

if (nr_seq_lote_processo_p IS NOT NULL AND nr_seq_lote_processo_p::text <> '') then
	-- se nao tiver registros na tabela manda gerar

	select	count(1)
	into STRICT	qt_conta_lote_w
	from	pls_cta_lote_proc_cta_temp;
	
	if (qt_conta_lote_w = 0) then
		CALL gera_cta_temp_lote_processo(nr_seq_lote_processo_p);
	end if;
end if;

-- quando for para calcular a taxa para pagamento

if (ie_tipo_tx_p = 'P') then
	CALL pls_taxa_intercambio_pck.pls_gerencia_tx_inter_pag(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
					nr_seq_conta_p, nr_seq_proc_p, nr_seq_mat_p,
					nm_usuario_p, cd_estabelecimento_p, nr_seq_analise_p);
else
	-- quando for para calcular a taxa para cobranca

	CALL pls_taxa_intercambio_pck.pls_gerencia_tx_inter_cob(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
					nr_seq_conta_p, nr_seq_proc_p, nr_seq_mat_p,
					nm_usuario_p, cd_estabelecimento_p, nr_seq_analise_p);
end if;
					
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
