CREATE OR REPLACE PROCEDURE pls_taxa_intercambio_pck.pls_gerencia_tx_inter_pag ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_mat_p pls_conta_mat.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type ) AS $body$
DECLARE

qt_retorno_w	integer;

BEGIN

--retorna a quantidade de contas de intercambio existente caso for 0 nao executa nenhum dos processos

qt_retorno_w	:= pls_taxa_intercambio_pck.obter_qt_contas_intercambio(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
						nr_seq_conta_p, nr_seq_proc_p, nr_seq_mat_p, nr_seq_analise_p);
						
if (qt_retorno_w	> 0) then
	--Limpa a regra e taxa de intercambio dos procedimentos e/ou materiais.

	CALL pls_taxa_intercambio_pck.limpa_taxa_intercambio(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
				nr_seq_conta_p, nr_seq_proc_p, nr_seq_mat_p, nr_seq_analise_p);				
				
	--Grava taxa de intercambio considerando contrato de intercambio(OPS - Contratos de intercambio), as regras

	-- de intercambio aqui cadastradas, tem prioridade sobre as demais(Operadoras congeneres e cooperativas medicas).

	CALL pls_taxa_intercambio_pck.grava_taxa_inter_contrato(	nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
					nr_seq_conta_p, nr_seq_proc_p, nr_seq_mat_p, nr_seq_analise_p);				

	--Vincula as contas aos tipos de regras,  gravando na tabela pls_aux_tx_inter.

	CALL pls_taxa_intercambio_pck.vincula_conta_tipo_regra(nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p, nm_usuario_p, nr_seq_analise_p);

	--Grava taxas de intercambio considerando as regras cadastradas em cooperativas medicas e operadoras congeneres

	CALL pls_taxa_intercambio_pck.grava_tx_inter_coop_operadora(nr_seq_proc_p,nr_seq_mat_p);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
