CREATE OR REPLACE FUNCTION pls_termo_beneficiario_pck.obter_se_benef_possui_termo ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_termo_p pls_termo_beneficiario.nr_sequencia%type) RETURNS char AS $body$
DECLARE


qt_contador_w	smallint;
ds_retorno_w	char(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_contador_w
from	pls_segurado_termo
where	nr_seq_segurado	= nr_seq_segurado_p
and	nr_seq_termo	= nr_seq_termo_p
and	(dt_confirmacao IS NOT NULL AND dt_confirmacao::text <> '');

if (qt_contador_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

END;

$body$
