CREATE OR REPLACE PROCEDURE pls_tipos_cta_val_pck.atualiza_sel_ie_valido_temp (nr_id_transacao_p pls_rp_cta_selecao.nr_id_transacao%type, dados_filtro_p pls_tipos_cta_val_pck.dados_filtro, ie_filtro_validacao_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Atualizar o IE_VALIDO_TEMP para N  no comeco de cada aplicacao de filtro das regras das ocorrencias.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Usar sempre o nr_id_transacao
------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN
-- As acoes de filtro e validacao devem ser separadas para que a atualizacao do IE_VALIDO_TEMP seja feita da forma correta para cada situacao.

if (ie_filtro_validacao_p = 'F') then

	update	pls_rp_cta_selecao
	set	ie_valido_temp = 'N'
	where	nr_id_transacao = nr_id_transacao_p
	and	nr_seq_rp_cta_filtro = dados_filtro_p.nr_sequencia;
-- Quando for uma validacao o campo IE_VALIDO_TEMP deve ser atualizado para N em todos os registros quando for chamado esta procedure.

elsif (ie_filtro_validacao_p = 'V') then	

	update	pls_rp_cta_selecao
	set	ie_valido_temp = 'N'
	where	nr_id_transacao = nr_id_transacao_p;
end if;

END;

$body$
