CREATE OR REPLACE PROCEDURE pls_tipos_cta_val_pck.atualizar_regra_proc ( nr_seq_proc_p dbms_sql.number_table, nr_seq_regra_p dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

if (nr_seq_proc_p.count > 0) then
	-- Aplica a regra de valorizacao especial

	forall i in nr_seq_proc_p.first..nr_seq_proc_p.last
		update	pls_conta_proc
		set	nr_seq_rp_combinada = nr_seq_regra_p(i),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_proc_p(i);
		
	commit;
end if;

END;

$body$
