CREATE OR REPLACE PROCEDURE pls_tipos_cta_val_pck.pls_cta_val_limpa_regra ( nr_seq_lote_conta_p pls_protocolo_conta.nr_seq_lote%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					
tb_sequencia_w	pls_util_cta_pck.t_number_table;					
					
C01 CURSOR(	nr_seq_lote_pc			pls_lote_protocolo_conta.nr_sequencia%type,
		nr_seq_protocolo_pc		pls_protocolo_conta.nr_sequencia%type,
		nr_seq_lote_processo_pc		pls_cta_lote_processo.nr_sequencia%type,
		nr_seq_conta_pc			pls_conta.nr_sequencia%type,
		nr_seq_conta_proc_pc		pls_conta_proc.nr_sequencia%type,
		nr_seq_analise_pc		pls_analise_conta.nr_sequencia%type) FOR
		
	SELECT	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_cta_val_v a		
	where	a.nr_seq_lote_conta = nr_seq_lote_pc
	
union all

	SELECT	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_cta_val_v a
	where	a.nr_seq_protocolo = nr_seq_protocolo_pc
	
union all

	select	a.nr_sequencia nr_seq_conta_proc
	from	pls_cta_lote_proc_cta_temp b,
		pls_conta_proc_cta_val_v a
        where  	a.nr_seq_conta = b.nr_seq_conta
	
union all

	select	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_cta_val_v a
	where	a.nr_seq_conta = nr_seq_conta_pc
	and	coalesce(nr_seq_conta_proc_pc::text, '') = ''
	
union all

	select	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_cta_val_v a
        where  	a.nr_sequencia = nr_seq_conta_proc_pc
	
union all

	select	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_cta_val_v a
        where  	a.nr_seq_analise = nr_seq_analise_pc;


BEGIN

open C01(nr_seq_lote_conta_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p);
loop	
	-- Limpa os registros da lista para que nao seja processado o mesmo registro mais de uma vez.

	tb_sequencia_w.delete;
	
	-- Executa o fetch de um numero determinado de linhas do cursor na lista.

	fetch C01 bulk collect into tb_sequencia_w limit pls_util_cta_pck.qt_registro_transacao_w;

	-- Quando o cursor nao tiver mais linhas entao sai do loop.

	exit when tb_sequencia_w.count = 0;
				
	--Faz update dos registros(delimitados)

	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		update	pls_conta_proc
		set	nr_seq_rp_combinada	 = NULL		
		where	nr_sequencia		= tb_sequencia_w(i);
	commit;
end loop;
close C01;

tb_sequencia_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
