SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.aplica_filtro_conta ( ie_considera_selecao_p boolean, dados_regra_p pls_tipos_ocor_pck.dados_regra, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, dados_consistencia_p pls_tipos_ocor_pck.dados_consistencia, dados_forma_geracao_ocor_p pls_tipos_ocor_pck.dados_forma_geracao_ocor, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: gerencia a aplicacao do filtro de conta
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[  X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Nao incluir restricoes nessa procedure, ela e responsavel apenas  por passar no cursor
dos filtros e incluir na selecao das contas que devem ter a ocorrencia gerada.

Alteracoes:
 ------------------------------------------------------------------------------------------------------------------

 usuario OS XXXXXX 01/01/2000 -
 Alteracao:	Descricao da alteracao.
Motivo:	Descricao do motivo.
 ------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


tb_seq_conta_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_proc_w	pls_util_cta_pck.t_number_table;
tb_seq_conta_mat_w	pls_util_cta_pck.t_number_table;
tb_seq_selecao_w	pls_util_cta_pck.t_number_table;
tb_guia_referencia_w	pls_util_cta_pck.t_varchar2_table_20;
tb_seq_segurado_w	pls_util_cta_pck.t_number_table;
tb_dt_item_w		pls_util_cta_pck.t_date_table;
tb_dt_item_hora_ini_w	pls_util_cta_pck.t_date_table;
tb_dt_item_hora_fim_w	pls_util_cta_pck.t_date_table;
tb_dt_item_dia_ini_w	pls_util_cta_pck.t_date_table;
tb_dt_item_dia_fim_w	pls_util_cta_pck.t_date_table;
tb_ie_origem_proced_w	pls_util_cta_pck.t_number_table;
tb_cd_procedimento_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_material_w	pls_util_cta_pck.t_number_table;
tb_ie_internado_w	pls_util_cta_pck.t_varchar2_table_1;
tb_ie_vinc_internado_w	pls_util_cta_pck.t_varchar2_table_1;
tb_cd_versao_tiss_w	pls_util_cta_pck.t_varchar2_table_20;
tb_ie_carater_inter_w	pls_util_cta_pck.t_varchar2_table_20;
tb_ie_carater_inter_imp_w	pls_util_cta_pck.t_varchar2_table_20;


qt_reg_w			integer;
tb_seq_conta_ger_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_proc_ger_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_mat_ger_w		pls_util_cta_pck.t_number_table;
tb_seq_selecao_ger_w		pls_util_cta_pck.t_number_table;
tb_guia_referencia_ger_w	pls_util_cta_pck.t_varchar2_table_20;
tb_seq_segurado_ger_w		pls_util_cta_pck.t_number_table;
tb_dt_item_ger_w		pls_util_cta_pck.t_date_table;
tb_dt_item_hora_ini_ger_w	pls_util_cta_pck.t_date_table;
tb_dt_item_hora_fim_ger_w	pls_util_cta_pck.t_date_table;
tb_dt_item_dia_ini_ger_w	pls_util_cta_pck.t_date_table;
tb_dt_item_dia_fim_ger_w	pls_util_cta_pck.t_date_table;
tb_ie_origem_proced_ger_w	pls_util_cta_pck.t_number_table;
tb_cd_procedimento_ger_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_material_ger_w	pls_util_cta_pck.t_number_table;

dados_filtro_conta_w	pls_tipos_ocor_pck.dados_filtro_conta;
ds_select_w		varchar(8000);
valor_bind_w		sql_pck.t_dado_bind;
dados_restricao_w	pls_tipos_ocor_pck.dados_select;
dados_restricao_proc_w	pls_tipos_ocor_pck.dados_select;
dados_restricao_mat_w	pls_tipos_ocor_pck.dados_select;
cursor_w		sql_pck.t_cursor;
ie_registro_valido_w	boolean;

c_filtro CURSOR(	nr_seq_filtro_pc	pls_oc_cta_filtro.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.ie_tipo_guia,
		a.cd_doenca_cid,
		a.nr_seq_tipo_atendimento,
		a.ie_regime_internacao,
		a.ie_tipo_internado,
		a.ie_carater_internacao,
		a.ie_tipo_consulta,
		a.nr_seq_saida_int,
		a.nr_seq_clinica,
		a.ie_origem_conta,
		a.ie_tipo_conta,
		a.ie_internacao_obstetrica,
		a.nr_seq_tipo_acomod_conta,
		a.nr_seq_grupo_doenca,
		a.ie_internado,
		a.ie_indicador_dorn,
		ie_vinc_internacao,
		ie_recem_nascido,
		a.ie_tipo_faturamento,
		a.ie_indicacao_acidente,
		a.ie_acomodacao_autorizada,
		a.ie_conta_autorizada,
		a.tp_rede_min,
		a.ie_regime_atendimento,
		a.ie_saude_ocupacional
	from	pls_oc_cta_filtro_conta a
	where	a.nr_seq_oc_cta_filtro	= nr_seq_filtro_pc
	and	a.ie_situacao = 'A';

BEGIN

-- Se nao tiver informacoes da regra nao sera possivel aplicar os filtros.

if (dados_filtro_p.nr_sequencia IS NOT NULL AND dados_filtro_p.nr_sequencia::text <> '') then

	-- Atualizar o campo auxiliar que sera utilizado para sinalizar os registros que foram processados

	CALL CALL CALL CALL CALL pls_tipos_ocor_pck.atualiza_campo_auxiliar( 'F', nr_id_transacao_p, dados_filtro_p, dados_regra_p);

	-- Abrir vetor com todas as combinacoes de filtro da regra

	for r_c_filtro in c_filtro(dados_filtro_p.nr_sequencia) loop

		-- Preencher dados do filtro para passar como parametro

		dados_filtro_conta_w.nr_sequencia		:= r_c_filtro.nr_sequencia;
		dados_filtro_conta_w.ie_tipo_guia		:= r_c_filtro.ie_tipo_guia;
		dados_filtro_conta_w.cd_doenca_cid		:= r_c_filtro.cd_doenca_cid;
		dados_filtro_conta_w.nr_seq_tipo_atendimento	:= r_c_filtro.nr_seq_tipo_atendimento;
		dados_filtro_conta_w.ie_regime_internacao	:= r_c_filtro.ie_regime_internacao;
		dados_filtro_conta_w.ie_tipo_internado		:= r_c_filtro.ie_tipo_internado;
		dados_filtro_conta_w.ie_carater_internacao	:= r_c_filtro.ie_carater_internacao;
		dados_filtro_conta_w.ie_tipo_consulta		:= r_c_filtro.ie_tipo_consulta;
		dados_filtro_conta_w.nr_seq_saida_int		:= r_c_filtro.nr_seq_saida_int;
		dados_filtro_conta_w.nr_seq_clinica		:= r_c_filtro.nr_seq_clinica;
		dados_filtro_conta_w.ie_origem_conta		:= r_c_filtro.ie_origem_conta;
		dados_filtro_conta_w.ie_tipo_conta		:= r_c_filtro.ie_tipo_conta;
		dados_filtro_conta_w.ie_internacao_obstetrica	:= r_c_filtro.ie_internacao_obstetrica;
		dados_filtro_conta_w.nr_seq_tipo_acomod_conta	:= r_c_filtro.nr_seq_tipo_acomod_conta;
		dados_filtro_conta_w.nr_seq_grupo_doenca	:= r_c_filtro.nr_seq_grupo_doenca;
		dados_filtro_conta_w.ie_internado		:= r_c_filtro.ie_internado;
		dados_filtro_conta_w.ie_indicador_dorn		:= r_c_filtro.ie_indicador_dorn;
		dados_filtro_conta_w.ie_vinc_internacao		:= r_c_filtro.ie_vinc_internacao;
		dados_filtro_conta_w.ie_recem_nascido		:= r_c_filtro.ie_recem_nascido;
		dados_filtro_conta_w.ie_tipo_faturamento	:= r_c_filtro.ie_tipo_faturamento;
		dados_filtro_conta_w.ie_indicacao_acidente	:= r_c_filtro.ie_indicacao_acidente;
		dados_filtro_conta_w.ie_acomodacao_autorizada	:= r_c_filtro.ie_acomodacao_autorizada;
		dados_filtro_conta_w.ie_conta_autorizada	:= r_c_filtro.ie_conta_autorizada;
		dados_filtro_conta_w.tp_rede_min		:= r_c_filtro.tp_rede_min;
		dados_filtro_conta_w.ie_regime_atendimento		:= r_c_filtro.ie_regime_atendimento;
		dados_filtro_conta_w.ie_saude_ocupacional		:= r_c_filtro.ie_saude_ocupacional;

		begin
			-- so respeita a incidencia da regra PM para filtros que nao sao excecao

			-- um filtro de excecao ira atingir somente a sua incidencia e jamais sera PM

			if	(dados_regra_p.ie_incidencia_selecao = 'PM' AND dados_filtro_p.ie_processo_excecao = 'N') then
				-- restricao para o filtro de procedimento

				valor_bind_w := pls_tipos_ocor_pck.obter_restricao_conta(	dados_regra_p, dados_filtro_p, dados_filtro_conta_w, 'PROC', valor_bind_w);
				-- restricao para o filtro de material

				valor_bind_w := pls_tipos_ocor_pck.obter_restricao_conta(		dados_regra_p, dados_filtro_p, dados_filtro_conta_w, 'MAT', valor_bind_w);
			else
				-- restricao para todas as outras situacoes

				valor_bind_w := pls_tipos_ocor_pck.obter_restricao_conta(	dados_regra_p, dados_filtro_p, dados_filtro_conta_w, null, valor_bind_w);
			end if;

			-- Montar o select padrao juntamente as restricoes.

			valor_bind_w := pls_tipos_ocor_pck.obter_select_filtro(	ie_considera_selecao_p, nr_id_transacao_p, dados_consistencia_p, dados_regra_p, dados_forma_geracao_ocor_p, dados_filtro_p, dados_restricao_w, dados_restricao_proc_w, dados_restricao_mat_w, cd_estabelecimento_p, valor_bind_w);

			-- executa o comando sql com os respectivos binds

			valor_bind_w := sql_pck.executa_sql_cursor(	ds_select_w, valor_bind_w);

			loop
				fetch cursor_w bulk collect into	tb_seq_conta_w, tb_seq_conta_proc_w,
									tb_seq_conta_mat_w, tb_guia_referencia_w,
									tb_seq_segurado_w, tb_dt_item_w,
									tb_dt_item_hora_ini_w, tb_dt_item_hora_fim_w,
									tb_dt_item_dia_ini_w, tb_dt_item_dia_fim_w,
									tb_ie_origem_proced_w, tb_cd_procedimento_w,
									tb_nr_seq_material_w, tb_seq_selecao_w,
									tb_ie_internado_w, tb_ie_vinc_internado_w,
									tb_ie_carater_inter_w, tb_ie_carater_inter_imp_w,
									tb_cd_versao_tiss_w
				limit pls_util_pck.qt_registro_transacao_w;
				exit when tb_seq_conta_w.count = 0;


				qt_reg_w := 0;
				tb_seq_conta_ger_w.delete;
				tb_seq_conta_proc_ger_w.delete;
				tb_seq_conta_mat_ger_w.delete;
				tb_seq_selecao_ger_w.delete;
				tb_guia_referencia_ger_w.delete;
				tb_seq_segurado_ger_w.delete;
				tb_dt_item_ger_w.delete;

				for i in tb_seq_conta_w.first .. tb_seq_conta_w.last loop

					-- verifica se o registro e valido

					-- feito por performance para nao usar functions em clausulas where

					ie_registro_valido_w := pls_tipos_ocor_pck.ie_reg_valido_fil_conta( dados_filtro_conta_w, dados_regra_p,
											tb_ie_internado_w(i), tb_ie_vinc_internado_w(i),
											tb_ie_carater_inter_w(i), tb_ie_carater_inter_imp_w(i),
											tb_cd_versao_tiss_w(i));

					-- se for um registro valido grava na variavel que sera enviada para o banco como valida

					if (ie_registro_valido_w) then

						tb_seq_conta_ger_w(qt_reg_w) := tb_seq_conta_w(i);
						tb_seq_conta_proc_ger_w(qt_reg_w) := tb_seq_conta_proc_w(i);
						tb_seq_conta_mat_ger_w(qt_reg_w) := tb_seq_conta_mat_w(i);
						tb_seq_selecao_ger_w(qt_reg_w) := tb_seq_selecao_w(i);
						tb_guia_referencia_ger_w(qt_reg_w) := tb_guia_referencia_w(i);
						tb_seq_segurado_ger_w(qt_reg_w) := tb_seq_segurado_w(i);
						tb_dt_item_ger_w(qt_reg_w) := tb_dt_item_w(i);
						tb_dt_item_hora_ini_ger_w(qt_reg_w) := tb_dt_item_hora_ini_w(i);
						tb_dt_item_hora_fim_ger_w(qt_reg_w) := tb_dt_item_hora_fim_w(i);
						tb_dt_item_dia_ini_ger_w(qt_reg_w) := tb_dt_item_dia_ini_w(i);
						tb_dt_item_dia_fim_ger_w(qt_reg_w) := tb_dt_item_dia_fim_w(i);
						tb_ie_origem_proced_ger_w(qt_reg_w) := tb_ie_origem_proced_w(i);
						tb_cd_procedimento_ger_w(qt_reg_w) := tb_cd_procedimento_w(i);
						tb_nr_seq_material_ger_w(qt_reg_w) := tb_nr_seq_material_w(i);

						qt_reg_w := qt_reg_w + 1;
					end if;
				end loop;

				-- Insere todos os registros das listas na tabela de selecao

				CALL CALL CALL CALL CALL pls_tipos_ocor_pck.gerencia_selecao_filtro(	'F', tb_seq_conta_ger_w,
								tb_seq_conta_proc_ger_w, tb_seq_conta_mat_ger_w,
								tb_guia_referencia_ger_w, tb_seq_segurado_ger_w,
								tb_dt_item_ger_w, tb_dt_item_hora_ini_ger_w,
								tb_dt_item_hora_fim_ger_w, tb_dt_item_dia_ini_ger_w,
								tb_dt_item_dia_fim_ger_w, tb_ie_origem_proced_ger_w,
								tb_cd_procedimento_ger_w, tb_nr_seq_material_ger_w,
								tb_seq_selecao_ger_w, nr_id_transacao_p,
								'S', nm_usuario_p,
								dados_filtro_p, dados_regra_p);
			end loop;
			close cursor_w;

		exception
			when others then

			if (cursor_w%isopen) then
				close cursor_w;
			end if;

			-- Tratar erro gerado no sql dinamico, sera inserido registro no log e abortado o processo exibindo mensagem de erro.

			CALL pls_tipos_ocor_pck.trata_erro_sql_dinamico(	dados_regra_p, ds_select_w,
									nr_id_transacao_p, nm_usuario_p);
		end;
	end loop; -- filtro

	-- Atualizar o campo definitivo que sera utilizado para sinalizar os registros que foram processados

	CALL CALL CALL CALL CALL pls_tipos_ocor_pck.atualiza_campo_valido(	'F', nr_id_transacao_p, dados_filtro_p, dados_regra_p);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.aplica_filtro_conta ( ie_considera_selecao_p boolean, dados_regra_p pls_tipos_ocor_pck.dados_regra, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, dados_consistencia_p pls_tipos_ocor_pck.dados_consistencia, dados_forma_geracao_ocor_p pls_tipos_ocor_pck.dados_forma_geracao_ocor, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
