 ------------------------------------------------------------------------------------------------------------------

 usuario OS XXXXXX 01/01/2000 -
 Alteracao:	Descricao da alteracao.
Motivo:	Descricao do motivo.
 ------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


tb_seq_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_id_transacao_ex_pc	pls_selecao_ex_ocor_cta.nr_id_transacao%type) FOR
	SELECT	nr_sequencia nr_seq_selecao
	from	pls_selecao_ex_ocor_cta
	where	nr_id_transacao = nr_id_transacao_ex_pc
	and	ie_valido = 'S';

BEGIN

open c01(nr_id_transacao_ex_p);
loop
	tb_seq_w.delete;

	fetch c01 bulk collect into tb_seq_w
	limit pls_util_pck.qt_registro_transacao_w;

	exit when tb_seq_w.count = 0;

	forall i in tb_seq_w.first .. tb_seq_w.last
		update	pls_selecao_ex_ocor_cta
		set	ie_valido_temp = 'N'
		where	nr_sequencia = tb_seq_w(i);

	
end loop;
close c01;

END;

$body$
