SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.atualiza_campo_valido ( ie_filtro_validacao_p text, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, dados_regra_p pls_tipos_ocor_pck.dados_regra, ie_filtro_e_p text default 'N', ie_ignora_excecao_p text default 'N') AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Atualizar os campos relacionados a informar que o registro e valido que sao responsaveis por auxiliar no controle do que
	deve ser validado
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Usar sempre o nr_id_transacao

Alteracoes:
 ------------------------------------------------------------------------------------------------------------------

 usuario OS XXXXXX 01/01/2000 -
 Alteracao:	Descricao da alteracao.
Motivo:	Descricao do motivo.
 ------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

ie_valido_w		varchar(1);


BEGIN
-- se estiver sendo processado um filtro

if (ie_filtro_validacao_p = 'F') then

	-- nunca pode entrar aqui quando for excecao de qualquer maneira

	if (dados_filtro_p.ie_processo_excecao = 'N') then

		-- filtro bom em regra boa usa os campos ie_valido e ie_valido_temp

		update	pls_selecao_ocor_cta
		set	ie_valido = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_valido_temp = 'N'
		and	nr_seq_filtro = dados_filtro_p.nr_sequencia;
		
	-- se for regra ou filtro de excecao e necessario fazer uma preparacao nos registros que serao selecionados

	-- no caso de filtros de prestadores e profissionais, nao deve fazer nada na regra de excecao

	elsif	(dados_filtro_p.ie_processo_excecao = 'S' AND ie_ignora_excecao_p = 'N') then

		-- o ultimo processo executado para as regras do tipo "E"

		if (ie_filtro_e_p = 'F') then

			-- filtro de excecao com regra de excecao valida o que estava invalido, por isso vira S

			if (dados_regra_p.ie_excecao = 'S' and dados_filtro_p.ie_excecao = 'S') then
				ie_valido_w := 'S';
			else
				ie_valido_w := 'N';
			end if;

			-- tudo o que casou nas regras de excecao deve ser invalidado

			update	pls_selecao_ocor_cta
			set	ie_valido = ie_valido_w
			where	nr_id_transacao = nr_id_transacao_p
			and	ie_excecao = 'S';
			

		else
			-- coloca ie_excecao = N para todos os registros que nao casaram com nenhum filtro

			update	pls_selecao_ocor_cta
			set	ie_excecao = 'N'
			where	nr_id_transacao = nr_id_transacao_p
			and	ie_excecao = 'S'
			and	ie_valido_temp = 'N';
			
		end if;
	end if;

-- se estiver sendo processada uma validacao

elsif (ie_filtro_validacao_p = 'V') then
	-- se for regra de excecao, tudo o que foi selecionado faz o ie_valido ficar N

	if (dados_regra_p.ie_excecao = 'S') then
		ie_valido_w := 'S';
	else
		-- se for regra boa, tudo o que nao foi selecionado faz o ie_valido ficar N

		ie_valido_w := 'N';
	end if;

	update	pls_selecao_ocor_cta
	set	ie_valido = 'N'
	where	nr_id_transacao = nr_id_transacao_p
	and	ie_valido_temp = ie_valido_w;
	
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.atualiza_campo_valido ( ie_filtro_validacao_p text, nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, dados_regra_p pls_tipos_ocor_pck.dados_regra, ie_filtro_e_p text default 'N', ie_ignora_excecao_p text default 'N') FROM PUBLIC;
