CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.atualiza_sel_ie_valido ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, ie_filtro_validacao_p text, dados_regra_p dados_regra default null) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Atualizar o IE_VALIDO para N quando o IE_VALIDO_TEMP  for N  no comeco de cada aplicacao de filtro das regras das ocorrencias.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Usar sempre o nr_id_transacao

Alteracoes:
------------------------------------------------------------------------------------------------------------------

jjung OS 601977 - 	Criacao da procedure.
------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */



BEGIN
-- As acoes de filtro e validacao devem ser separadas para que a atualizacao do IE_VALIDO_TEMP seja feita da forma correta para cada situacao.

if (dados_regra_p.ie_excecao = 'S') then

	if (ie_filtro_validacao_p = 'F') then

		-- atualizacao padrao para filtros.

		-- Sera atualizado o campo IE_VALIDO para N onde o campo IE_VALIDO_TEMP for igual a N para filtros que nao sao excecao.

		-- Sera atualizado o campo IE_EXCECAO para  N onde o campo IE_EXCECAO_TEMP for N nos casos de filtros de excecao.

		CALL pls_tipos_ocor_pck.atualiza_sel_valido_filtro_exc(nr_id_transacao_p, dados_filtro_p);
	else
		update	pls_selecao_ocor_cta
		set	ie_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao_temp = 'N';
		
	end if;
else
	if (ie_filtro_validacao_p = 'F') then

		-- Atualizar os padroes para filtros em regra de excecao.

		-- Sera atualizado o campo IE_EXCECAO para N onde o campo IE_EXCECAO_TEMP for igual a N para filtros que nao sao excecao.

		-- Sera atualizado o campo IE_EXCECAO_EXCECAO para N onde o campo IE_EXCECAO_EXCECAO_TEMP for igual a N para filtros que sao excecao.

		CALL pls_tipos_ocor_pck.atualiza_sel_valido_filtro(nr_id_transacao_p, dados_filtro_p);

	-- Quando for uma validacao o campo IE_VALIDO deve ser atualizado para N em todos os registros em que o IE_VALIDO_TEMP estiver como N quando for chamado esta procedure.

	elsif (ie_filtro_validacao_p = 'V') then

		update	pls_selecao_ocor_cta
		set	ie_valido = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_valido_temp = 'N';
		
	end if;
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.atualiza_sel_ie_valido ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro, ie_filtro_validacao_p text, dados_regra_p dados_regra default null) FROM PUBLIC;
