CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.atualiza_sel_temp_filtro_exc ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Atualizar os valores do campo  ie_valido_temp ou ie_excecao_temp para os filtros da ocorrencia
	no comeco do processo para indicacao de quem foi selecionado e quem nao foi pelo filtro.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Usar sempre o nr_id_transacao

Alteracoes:
------------------------------------------------------------------------------------------------------------------

jjung OS 693338 - 29/01/2014 - 	Criacao da procedure.
------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */



BEGIN

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then

	-- Se for filtro comum trabalha no primeiro nivel de excecao com o campo IE_EXCECAO_TEMP

	if (dados_filtro_p.ie_excecao = 'N') then

		update	pls_selecao_ocor_cta
		set	ie_excecao_temp = 'N'
		where	nr_id_transacao = nr_id_transacao_p;
		

	-- Se for filtro de excecao trabalha no seguindo nivel de excecao com o campo IE_EXCECAO_EXCECAO_TEMP

	elsif (dados_filtro_p.ie_excecao = 'S') then

		update	pls_selecao_ocor_cta
		set	ie_excecao_excecao_temp = 'N'
		where	nr_id_transacao = nr_id_transacao_p;
		
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.atualiza_sel_temp_filtro_exc ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) FROM PUBLIC;
