SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.atualiza_sel_valido_filtro_exc ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) AS $body$
DECLARE


-- Atualizar o campo IE_EXCECAO em filtros na regra de excecao.


BEGIN

-- So precessa se for valido, principio basico.

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then

	-- Se for um filtro comum vai nos campos IE_EXCECAO e IE_EXCECAO_TEMP

	if (dados_filtro_p.ie_excecao = 'N') then

		update	pls_selecao_ocor_cta
		set	ie_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao_temp = 'N';
		

	-- Se for filtro de excecao vai nos campos IE_EXCECAO_EXCECAO e IE_EXCECAO_EXCECAO_TEMP.

	elsif (dados_filtro_p.ie_excecao = 'S') then

		update	pls_selecao_ocor_cta
		set	ie_excecao_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao_excecao_temp = 'N';
		
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.atualiza_sel_valido_filtro_exc ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) FROM PUBLIC;
