CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.atualiza_sel_valido_filtro ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) AS $body$
DECLARE


-- Atualizar o campo IE_EXCECAO em filtros comuns.


BEGIN
-- So precessa se for valido, principio basico.

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then

	-- Se o filtro nao for um filtro de excecao entao ele so podera alterar os registros dele mesmo.

	-- Os filtros de Exececao nao devem atualizar o IE_VALIDO_TEMP  pois os filtros de excecao irao retornar os registros que nao deve ser gerada a ocorrencia, atualizando o campo

	-- IE_VALIDO para N.

	if (dados_filtro_p.ie_excecao = 'N') then

		update	pls_selecao_ocor_cta
		set	ie_valido = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_valido_temp = 'N'
		and	nr_seq_filtro = dados_filtro_p.nr_sequencia;
		

	-- Filtros de excecao em regras comuns so mexem no campo IE_EXCECAO.

	elsif (dados_filtro_p.ie_excecao = 'S') then

		update	pls_selecao_ocor_cta
		set	ie_excecao = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao_temp = 'N';
		
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tipos_ocor_pck.atualiza_sel_valido_filtro ( nr_id_transacao_p pls_selecao_ocor_cta.nr_id_transacao%type, dados_filtro_p pls_tipos_ocor_pck.dados_filtro) FROM PUBLIC;
