CREATE OR REPLACE PROCEDURE pls_tipos_ocor_pck.limpa_selecao_excecao ( nr_id_transacao_ex_p pls_selecao_ex_ocor_cta.nr_id_transacao%type) AS $body$
DECLARE


tb_seq_w	pls_util_cta_pck.t_number_table;

-- retorna as sequencias que devem ser apagadas para a tabela de excecao

c01 CURSOR(	nr_id_transacao_ex_pc	pls_selecao_ex_ocor_cta.nr_id_transacao%type) FOR
	SELECT	b.nr_sequencia
	from	pls_selecao_ex_ocor_cta b
	where	b.nr_id_transacao = nr_id_transacao_ex_pc;


BEGIN
-- apaga as excecoes

open c01(nr_id_transacao_ex_p);
loop
	tb_seq_w.delete;

	fetch c01 bulk collect into tb_seq_w
	limit pls_util_pck.qt_registro_transacao_w;

	exit when tb_seq_w.count = 0;

	forall i in tb_seq_w.first .. tb_seq_w.last
		delete	from pls_selecao_ex_ocor_cta
		where	nr_sequencia = tb_seq_w(i);

	
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
