CREATE OR REPLACE FUNCTION pls_tipos_ocor_pck.obter_qt_ocorrencia ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, dados_regra_p pls_tipos_ocor_pck.dados_regra) RETURNS integer AS $body$
DECLARE

qt_retorno_w	integer;

BEGIN

-- Para procedimento

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then

	select	count(1)
	into STRICT	qt_retorno_w
	from	pls_ocorrencia_benef a
	where	a.nr_seq_ocorrencia	= dados_regra_p.nr_seq_ocorrencia
	and	a.nr_seq_conta_proc	= nr_seq_conta_proc_p;
-- Para material

elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then

	select	count(1)
	into STRICT	qt_retorno_w
	from	pls_ocorrencia_benef a
	where	a.nr_seq_ocorrencia	= dados_regra_p.nr_seq_ocorrencia
	and	a.nr_seq_conta_mat	= nr_seq_conta_mat_p;
-- Para Conta

elsif (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

	select	count(1)
	into STRICT	qt_retorno_w
	from	pls_ocorrencia_benef a
	where	a.nr_seq_ocorrencia	= dados_regra_p.nr_seq_ocorrencia
	and	a.nr_seq_conta		= nr_seq_conta_p
	and	coalesce(a.nr_seq_conta_proc::text, '') = ''
	and	coalesce(a.nr_seq_conta_mat::text, '') = '';
end if;

return qt_retorno_w;

END;

$body$
