CREATE OR REPLACE FUNCTION pls_tipos_ocor_pck.obter_select_filtro_ex ( dados_filtro_p dados_filtro, ds_restricao_p pls_tipos_ocor_pck.dados_select, nr_id_transacao_ex_p pls_selecao_ex_ocor_cta.nr_id_transacao%type, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Cria os selects padroes de acordo com a aplicacao do filtro de excecao
	relacionado a todo o atendimento
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
 ------------------------------------------------------------------------------------------------------------------

 usuario OS XXXXXX 01/01/2000 -
 Alteracao:	Descricao da alteracao.
Motivo:	Descricao do motivo.
 ------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


ds_select_w			varchar(8000);


BEGIN

-- procedimento

if (dados_filtro_p.ie_incidencia_selecao = 'P') then

	ds_select_w :=	'select	sel.nr_sequencia ' || pls_util_pck.enter_w ||
			ds_restricao_p.ds_campos || pls_util_pck.enter_w ||
			'from	pls_selecao_ex_ocor_cta sel, '|| pls_util_pck.enter_w ||
			'	pls_conta_proc_ocor_v ' || current_setting('pls_tipos_ocor_pck.ds_alias_proc')::varchar(10) || pls_util_pck.enter_w ||
			ds_restricao_p.ds_tabelas || pls_util_pck.enter_w ||
			'where	sel.nr_id_transacao = :nr_id_transacao_ex'|| pls_util_pck.enter_w ||
			'and	sel.ie_valido =  ''S'' '|| pls_util_pck.enter_w ||
			'and	' || current_setting('pls_tipos_ocor_pck.ds_alias_proc')::varchar(10) || '.nr_sequencia = sel.nr_seq_conta_proc' || pls_util_pck.enter_w ||
			ds_restricao_p.ds_restricao || pls_util_pck.enter_w;
-- Por material

elsif (dados_filtro_p.ie_incidencia_selecao = 'M') then

	ds_select_w :=	'select	sel.nr_sequencia '|| pls_util_pck.enter_w ||
			ds_restricao_p.ds_campos || pls_util_pck.enter_w ||
			'from	pls_selecao_ex_ocor_cta sel, '|| pls_util_pck.enter_w ||
			'	pls_conta_mat_ocor_v ' || current_setting('pls_tipos_ocor_pck.ds_alias_mat')::varchar(10) || pls_util_pck.enter_w ||
			ds_restricao_p.ds_tabelas || pls_util_pck.enter_w ||
			'where	sel.nr_id_transacao = :nr_id_transacao_ex'|| pls_util_pck.enter_w ||
			'and	sel.ie_valido =  ''S'' '|| pls_util_pck.enter_w ||
			'and 	'|| current_setting('pls_tipos_ocor_pck.ds_alias_mat')::varchar(10) || '.nr_sequencia = sel.nr_seq_conta_mat' || pls_util_pck.enter_w ||
			ds_restricao_p.ds_restricao || pls_util_pck.enter_w;

-- por conta

else
	ds_select_w :=	'select	sel.nr_sequencia '|| pls_util_pck.enter_w ||
			ds_restricao_p.ds_campos || pls_util_pck.enter_w ||
			'from	pls_selecao_ex_ocor_cta sel, '|| pls_util_pck.enter_w ||
			'	pls_conta_ocor_v ' || current_setting('pls_tipos_ocor_pck.ds_alias_conta')::varchar(10) || pls_util_pck.enter_w ||
			ds_restricao_p.ds_tabelas || pls_util_pck.enter_w ||
			'where	sel.nr_id_transacao = :nr_id_transacao_ex'|| pls_util_pck.enter_w ||
			'and	sel.ie_valido =  ''S'' '|| pls_util_pck.enter_w ||
			'and	'|| current_setting('pls_tipos_ocor_pck.ds_alias_conta')::varchar(10) || '.nr_sequencia = sel.nr_seq_conta' || pls_util_pck.enter_w ||
			ds_restricao_p.ds_restricao || pls_util_pck.enter_w;

end if;

-- atribui a transacao para realizar os filtros somente no que foi gerado

valor_bind_p := sql_pck.bind_variable(	':nr_id_transacao_ex', nr_id_transacao_ex_p, valor_bind_p);

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_tipos_ocor_pck.obter_select_filtro_ex ( dados_filtro_p dados_filtro, ds_restricao_p pls_tipos_ocor_pck.dados_select, nr_id_transacao_ex_p pls_selecao_ex_ocor_cta.nr_id_transacao%type, valor_bind_p INOUT sql_pck.t_dado_bind) FROM PUBLIC;
