CREATE OR REPLACE FUNCTION pls_util_cta_pck.obter_nr_seq_cbo_espec_med ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna o CBO da especialidade medica.

		Primeiro e buscado o CBO com base na especialidade medica da pessoa fisica, aonde sera priorizado
		a menor prioridade da especialidade que contem o seq cbo.

		Caso nao encontre nenhuma, entao sera retornado o sequencial cadastrado diretamente na pessoa fisica

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


ds_retorno_w	medico_especialidade.nr_seq_cbo_saude%type := null;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_seq_cbo_saude)
	into STRICT	ds_retorno_w
	from	medico_especialidade
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_seq_prioridade	=	(SELECT	min(nr_seq_prioridade)
						from	medico_especialidade
						where	cd_pessoa_fisica	= cd_pessoa_fisica_p
						and	(nr_seq_cbo_saude IS NOT NULL AND nr_seq_cbo_saude::text <> ''));

	-- se nao encontrou nada, busca diretamente da pessoa fisica

	if (coalesce(ds_retorno_w::text, '') = '') then

		select	max(nr_seq_cbo_saude)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
