SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_util_cta_pck.obter_se_considera_proc_ref ( nr_seq_proc_ref_p pls_conta_proc.nr_seq_proc_ref%type) RETURNS varchar AS $body$
DECLARE



ie_status_proc_ref_w	pls_conta_proc.ie_status%type;


BEGIN

-- Se o procedimento nao tiver procedimento principal entao e para considerar

if (coalesce(nr_seq_proc_ref_p::text, '') = '') then

	return 'S';
else
	-- se tiver procedimento principal entao busca o status do mesmo.

	select	max(a.ie_status)
	into STRICT	ie_status_proc_ref_w
	from	pls_conta_proc a
	where	a.nr_sequencia = nr_seq_proc_ref_p;

	-- Se nao tiver este procedimento na base ou o mesmo estiver cancelado entao considera o procedimento.

	if (coalesce(ie_status_proc_ref_w::text, '') = '' or ie_status_proc_ref_w = 'D') then

		return 'S';
	else
		
		return 'N';
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
