CREATE OR REPLACE FUNCTION pls_util_cta_pck.obter_se_processa_glosa ( cd_motivo_tiss_p tiss_motivo_glosa.cd_motivo_tiss%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_processa_w		varchar(1);
qt_glosa_ativa_w	integer;


BEGIN

select	count(1)
into STRICT	qt_glosa_ativa_w
from	tiss_motivo_glosa  tiss
where	tiss.cd_motivo_tiss = cd_motivo_tiss_p
and	pls_obter_se_evento_glosa(tiss.nr_sequencia, null, cd_estabelecimento_p) = 'S';

-- se retornou algum registro significa que a glosa deve ser processada

if (qt_glosa_ativa_w > 0) then
	ie_processa_w := 'S';
else
	ie_processa_w := 'N';
end if;

return ie_processa_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
