CREATE OR REPLACE PROCEDURE pls_util_cta_pck.pls_abrir_proc_participante ( nr_seq_proc_origem_p pls_conta_proc.nr_sequencia%type, nr_seq_proc_aberto_p pls_conta_proc.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Esta procedure e responsavel por gerar os mesmos participantes do procedimento de origem nos procedimentos que acabaram de ser criados na procedure PLS_ABRIR_PROCEDIMENTO
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


C01 CURSOR(nr_seq_conta_proc_pc	pls_conta_proc.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_proc_participante_v
	where	nr_seq_conta_proc	= nr_seq_conta_proc_pc;

BEGIN

for r_C01 in C01(nr_seq_proc_origem_p) loop
	begin
	insert into pls_proc_participante(	nr_sequencia, cd_cbo_saude_imp, cd_cgc_imp, cd_guia,
			cd_medico, cd_medico_imp, cd_prestador_imp,
			dt_atualizacao, dt_atualizacao_nrec, ie_conselho_profissional,
			ie_funcao_medico_imp, nm_medico_executor_imp, nm_usuario,
			nm_usuario_nrec, nr_cpf_imp, nr_crm_imp,
			nr_seq_cbo_saude, nr_seq_conselho, nr_seq_conta_proc,
			nr_seq_grau_partic, nr_seq_honorario_crit, nr_seq_prestador_pgto,
			sg_conselho_imp, uf_crm_imp,
			vl_honorario_medico, vl_participante, ie_status,
			ie_gerada_cta_honorario )
		(SELECT	nextval('pls_proc_participante_seq'), cd_cbo_saude_imp, cd_cgc_imp, cd_guia,
			cd_medico, cd_medico_imp, cd_prestador_imp,
			dt_atualizacao, dt_atualizacao_nrec, ie_conselho_profissional,
			ie_funcao_medico_imp, nm_medico_executor_imp, nm_usuario,
			nm_usuario_nrec, nr_cpf_imp, nr_crm_imp,
			nr_seq_cbo_saude, nr_seq_conselho, nr_seq_proc_aberto_p,
			nr_seq_grau_partic, nr_seq_honorario_crit, nr_seq_prestador_pgto,
			sg_conselho_imp, uf_crm_imp,
			vl_honorario_medico, vl_participante, ie_status,
			ie_gerada_cta_honorario
		from	pls_proc_participante
		where	nr_sequencia = r_C01.nr_sequencia );
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
