-- retorna o status do pagamento atual do item em questao



CREATE OR REPLACE FUNCTION pls_util_cta_pck.pls_obter_stat_pag_item_calc ( nr_seq_conta_proc_p pls_conta_proc_v.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_v.nr_sequencia%type, nr_seq_proc_partic_p pls_proc_participante_v.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_pagamento_w		pls_conta_proc.ie_status_pagamento%type;

BEGIN
ie_pagamento_w := 'I';
-- busca os dados das view de acordo com o item

if (nr_seq_proc_partic_p IS NOT NULL AND nr_seq_proc_partic_p::text <> '') then

	select	max(ie_status_pagamento_calculado)
	into STRICT	ie_pagamento_w
	from	pls_proc_participante_v
	where	nr_sequencia	= nr_seq_proc_partic_p;

elsif (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then

	select	max(ie_status_pagamento_calculado)
	into STRICT	ie_pagamento_w
	from	pls_conta_proc_v
	where	nr_sequencia	= nr_seq_conta_proc_p;

elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then

	select	max(ie_status_pagamento_calculado)
	into STRICT	ie_pagamento_w
	from	pls_conta_mat_v
	where	nr_sequencia	= nr_seq_conta_mat_p;
end if;

return	ie_pagamento_w;

END;

$body$
