SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_util_pck.concate_dia_com_mes_ano ( dt_dia_p text, dt_mes_ano_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN
-- tenta concatenar com os dados que foram passados
begin

dt_retorno_w := to_date(dt_dia_p || '/' || dt_mes_ano_p, 'dd/mm/yyyy');

exception
when others then
	-- se der erro tenta pegar último dia do mês
	begin
		dt_retorno_w := last_day(to_date(dt_mes_ano_p, 'mm/yyyy'));
	exception
	when others then
		dt_retorno_w := null;
	end;
end;

return dt_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
