CREATE OR REPLACE FUNCTION pls_util_pck.get_grava_log_scs () RETURNS varchar AS $body$
BEGIN
-- controle para não fica executando o select todas as vezes dentro da mesma sessão
if	((current_setting('pls_util_pck.ie_gravar_log_scs_w')::pls_parametros_scs.ie_gravar_log_scs%coalesce(type::text, '') = '') or
	 (current_setting('pls_util_pck.ie_data_atu_log_scs_w')::timestamp <= (clock_timestamp() - interval '67 seconds'))) then
	select	coalesce(ie_gravar_log_scs, 'N')
	into STRICT	current_setting('pls_util_pck.ie_gravar_log_scs_w')::pls_parametros_scs.ie_gravar_log_scs%type
	from	pls_parametros_scs;

	PERFORM set_config('pls_util_pck.ie_data_atu_log_scs_w', clock_timestamp(), false);
end if;

return current_setting('pls_util_pck.ie_gravar_log_scs_w')::pls_parametros_scs.ie_gravar_log_scs%type;

END;

