SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_util_pck.obter_dt_vigencia_default (ie_tipo_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w timestamp;


BEGIN
-- se for I significa início, qualquer outra coisa é final
-- retorna uma data padrão de início para ser utilizada em vários lugares que necessitam deste tratamento
if (ie_tipo_p = 'I') then
	dt_retorno_w := to_date('31/12/1899', 'dd/mm/yyyy');
else
	dt_retorno_w := to_date('31/12/3000', 'dd/mm/yyyy');
end if;

return dt_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
