-- utilizada dentro de triggers para alimentar tratamentos de início e fim de vigência
CREATE OR REPLACE FUNCTION pls_util_pck.obter_dt_vigencia_null (dt_vigencia_p timestamp, ie_tipo_p text) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;

BEGIN
-- se for I significa início, qualquer outra coisa é final
if (ie_tipo_p = 'I') then
	dt_retorno_w := coalesce(dt_vigencia_p, pls_util_pck.obter_dt_vigencia_default(ie_tipo_p));
else
	dt_retorno_w := coalesce(dt_vigencia_p, pls_util_pck.obter_dt_vigencia_default(ie_tipo_p));
end if;

dt_retorno_w := trunc(dt_retorno_w);

return dt_retorno_w;

END;

