CREATE OR REPLACE FUNCTION pls_util_pck.obter_ultimo_dia_valido ( nr_dia_p bigint, dt_ref_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_mes_atual_w		varchar(2);
nr_dia_w		smallint;


BEGIN
nr_dia_w := nr_dia_p;

if (nr_dia_w > 28) then
	nr_mes_atual_w := to_char(dt_ref_p, 'mm');
	if (nr_mes_atual_w in ('04','06','09','11')) and (nr_dia_w > 30) then
		nr_dia_w := 30;
	elsif (nr_mes_atual_w = '02') and (nr_dia_w > 28) then
		if (obter_se_ano_bisexto(dt_ref_p) = 'S') then
			nr_dia_w := 29;
		else
			nr_dia_w := 28;
		end if;
	end if;
end if;

return nr_dia_w;

END;

$body$
