BEGIN


-- Realiza o Delete
forall i in nr_seq_item_p.first..nr_seq_item_p.last
	delete from w_pls_util_benef_item where nr_sequencia = nr_seq_item_p(i);
	
-- se solicitado, faz o commit (padrao sim)
