-- Gera os dados da utilizacao (AUTOMATIZADO)
CREATE OR REPLACE PROCEDURE pls_utilizacao_benef_pck.gerar_utilizacao_benef_aut ( nr_seq_ap_regra_benef_p pls_ap_regra_util_benef.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p text) AS $body$
DECLARE

					
nr_id_transacao_w	w_pls_utilizacao_benef.nr_id_transacao%type;

c01 CURSOR(	cd_estabelecimento_pc	estabelecimento.cd_estabelecimento%type) FOR
	SELECT	a.nr_seq_segurado
	from	pls_segurado_web a
	where	a.cd_estabelecimento = cd_estabelecimento_pc
	and	a.ie_situacao = 'A'
	and	(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '')
	
union

	SELECT	a.nr_seq_segurado
	from	wsuite_usuario a
	where	a.cd_estabelecimento = cd_estabelecimento_pc
	and	a.ie_situacao = 'A'
	and	(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '');
	
BEGIN
-- Limpa os dados antigos 
CALL pls_utilizacao_benef_pck.excluir_dados_util_antigos(cd_estabelecimento_p);

for r_c01_w in c01( cd_estabelecimento_p ) loop
	-- Gerar os dados
	nr_id_transacao_w := pls_utilizacao_benef_pck.gerar_utilizacao_benef( r_c01_w.nr_seq_segurado, null, null, nm_usuario_p, cd_estabelecimento_p, nr_id_transacao_w, nr_seq_ap_regra_benef_p);
end loop;

END;

$body$
