CREATE OR REPLACE PROCEDURE pls_utilizacao_benef_pck.gravar_dados_itens_util ( nr_sequencia_p INOUT pls_util_cta_pck.t_number_table, nr_seq_util_benef_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_proc_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_mat_p INOUT pls_util_cta_pck.t_number_table, qt_item_p INOUT pls_util_cta_pck.t_number_table, vl_item_p INOUT pls_util_cta_pck.t_number_table, nr_seq_grupo_ans_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_rec_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_proc_rec_p INOUT pls_util_cta_pck.t_number_table, nr_seq_conta_mat_rec_p INOUT pls_util_cta_pck.t_number_table, nm_usuario_p text, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Grava os itens da utilizacao	
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:

	Apenas realiza a gravacao dos itens, nenhuma regra de negocio deve ser realizada aqui
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_log_w		varchar(4000);
qt_error_w		integer;
idx_erro_w		integer;
dml_errors 		exception;


BEGIN

-- Grava os dados
begin
	forall i in nr_sequencia_p.first..nr_sequencia_p.last SAVE EXCEPTIONS
		insert into w_pls_util_benef_item(	nr_sequencia,
							nr_seq_util_benef,
							nr_seq_conta,
							nr_seq_conta_proc,
							nr_seq_conta_mat,
							qt_item,	
							vl_item,
							nr_seq_grupo_ans,
							nr_seq_conta_rec,
							nr_seq_conta_proc_rec,
							nr_seq_conta_mat_rec,
							nm_usuario,
							dt_atualizacao)
		values (	nr_sequencia_p(i),
				nr_seq_util_benef_p(i),
				nr_seq_conta_p(i),
				nr_seq_conta_proc_p(i),
				nr_seq_conta_mat_p(i),
				qt_item_p(i),
				vl_item_p(i),
				nr_seq_grupo_ans_p(i),
				nr_seq_conta_rec_p(i),
				nr_seq_conta_proc_rec_p(i),
				nr_seq_conta_mat_rec_p(i),
				nm_usuario_p,
				clock_timestamp());
exception
when SQLSTATE '50005' then
	qt_error_w := sql%bulk_exceptions.count;
	
	for idx_erro_w in 1..qt_error_w loop
		
		ds_log_w	:= 'Erro: '|| substr(sql%bulk_exceptions[idx_erro_w].error_code||' - '||sqlerrm(-(sql%bulk_exceptions[idx_erro_w].error_code))||' Callstack: '||dbms_utility.format_error_backtrace,1,4000);

		CALL pls_utilizacao_benef_pck.gravar_log_falha_util(ds_log_w);
	end loop;
end;

		
-- Se parametrizado, commita. O padrao tambem commita
if (coalesce(ie_commit_p, 'S') = 'S') then

	commit;
end if;

-- Se parametrizado, esvazia as tabelas em memoria
if (coalesce(ie_esvaziar_table_p, 'N') = 'S') then
	nr_sequencia_p.delete;
	nr_seq_util_benef_p.delete;
	nr_seq_conta_p.delete;
	nr_seq_conta_proc_p.delete;
	nr_seq_conta_mat_p.delete;
	qt_item_p.delete;
	vl_item_p.delete;
	nr_seq_grupo_ans_p.delete;
	nr_seq_conta_rec_p.delete;
	nr_seq_conta_proc_rec_p.delete;
	nr_seq_conta_mat_rec_p.delete;
end if;

END;

$body$
