CREATE OR REPLACE PROCEDURE pls_utilizacao_benef_pck.gravar_val_item_atend ( nr_sequencia_p INOUT pls_util_cta_pck.t_number_table, vl_item_p INOUT pls_util_cta_pck.t_number_table, ie_commit_p text, ie_esvaziar_tabela_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	grava o valor do atendimento
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:

	Apenas atualiza o valor de utilizacao do atendimento, e feito em forall para evitar
	as trocas de contexto.
	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

forall i in nr_sequencia_p.first..nr_sequencia_p.last
	update	w_pls_utilizacao_benef
	set	vl_utilizacao	= vl_item_p(i)
	where	nr_sequencia	= nr_sequencia_p(i);
	
if (coalesce(ie_commit_p, 'N') = 'S') then

	commit;
end if;

if (coalesce(ie_esvaziar_tabela_p, 'S') = 'S') then
	nr_sequencia_p.delete;
	vl_item_p.delete;
end if;

END;

$body$
