SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_utilizacao_benef_pck.obter_acesso_beneficiario ( nr_seq_segurado_p pls_segurado.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Verifica atraves de regras pre cadastradas se o beneficiario tem acesso a utilizacao

	Regras em Cadastro de regras >>  OPS - Contratos e beneficiarios >> Regra portal web >> Regra de utilizacao beneficiario
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(1) := 'N';
qt_regras_w	integer;

BEGIN

-- so verifica se foi informado um segurado
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	
	-- soma quantas regras que se encaixam par ao beneficiario que estao em vigencia
	select	sum(CASE WHEN coalesce(a.ie_permite_acesso,'N')='N' THEN  0  ELSE 1 END )
	into STRICT	qt_regras_w
	from	pls_regra_acess_benef	a,
		pls_segurado		b
	where	a.ie_tipo_segurado	= b.ie_tipo_segurado
	and	b.nr_sequencia		= nr_seq_segurado_p
	and	b.cd_estabelecimento	= a.cd_estabelecimento
	and	trunc(clock_timestamp(), 'DD')	between	trunc(a.dt_inicio_vigencia, 'DD') and fim_dia(a.dt_fim_vigencia);
	
	-- se existir alguma regra valida
	if (coalesce(qt_regras_w, 0) > 0) then
		
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
	
else
	ds_retorno_w := 'N';
end if; -- fim existe segurado
return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
