-- Escrever no arquivo
CREATE OR REPLACE PROCEDURE pls_utl_file_pck.escrever ( ds_conteudo_p text ) AS $body$
BEGIN
-- Verificar se o arquivo esta aberto para edi?
if (utl_file.is_open(ds_arquivo_w)) then
	-- Escrever a informa? no arquivo
	begin
	utl_file.put_line( ds_arquivo_w, ds_conteudo_p || chr(13));
	utl_file.fflush( ds_arquivo_w );
	exception
	when others then
		nr_seq_erro_w := 460840; -- O arquivo n?est?berto para edi?.
	end;
end if;

-- Caso o arquivo n?esteja editavel, barrar o processo
CALL pls_utl_file_pck.tratar_erro( null, nr_seq_erro_w);

END;

