-- Tratar a exibi? de erro
CREATE OR REPLACE PROCEDURE pls_utl_file_pck.tratar_erro ( ds_erro_p text, nr_seq_erro_p dic_objeto.nr_sequencia%type) AS $body$
BEGIN

if (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') or (nr_seq_erro_p IS NOT NULL AND nr_seq_erro_p::text <> '') then
	ds_erro_w := coalesce(ds_erro_p,wheb_mensagem_pck.get_texto(nr_seq_erro_p,null));
	
	-- Exibir o erro e barrar o processo
	CALL wheb_mensagem_pck.exibir_mensagem_abort(460257,'DS_ERRO=' || ds_erro_w);
end if;

END;

$body$
