CREATE OR REPLACE FUNCTION pls_via_acesso_pck.pls_se_excecao_via_acesso ( nr_seq_regra_p pls_regra_via_acesso.nr_sequencia%type, ie_origem_conta_p pls_regra_via_acesso.ie_origem_conta%type) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1)	:= 'N';
qt_excecao_w	integer;


BEGIN

select	count(1)
into STRICT	qt_excecao_w
from	pls_excecao_via_acesso
where	nr_seq_regra_via_acesso		= nr_seq_regra_p
and	((coalesce(ie_origem_conta::text, '') = '')or (ie_origem_conta	= ie_origem_conta_p));

if (qt_excecao_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

END;

