CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.alterar_auditor_responsavel ( nr_seq_visita_auditor_p pls_visita_tecnica_auditor.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_seq_visita_w		pls_visita_tecnica.nr_sequencia%type;
nm_usuario_auditor_w	pls_visita_tecnica_auditor.nm_usuario_auditor%type;

BEGIN
begin
select	nr_seq_visita,
	nm_usuario_auditor
into STRICT	nr_seq_visita_w,
	nm_usuario_auditor_w
from	pls_visita_tecnica_auditor
where	nr_sequencia	= nr_seq_visita_auditor_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

update	pls_visita_tecnica_auditor
set	ie_auditor_responsavel	= 'N',
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_seq_visita		= nr_seq_visita_w
and	nr_sequencia		<> nr_seq_visita_auditor_p
and	ie_auditor_responsavel	= 'S';

update	pls_visita_tecnica_auditor
set	ie_auditor_responsavel	= 'S',
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_visita_auditor_p;

CALL CALL pls_visita_tecnica_prest_pck.gravar_log_sistema(	nr_seq_visita_p	=> nr_seq_visita_w,
				ie_tipo_log_p	=> 2,
				ds_log_p	=> wheb_mensagem_pck.get_texto(1207552, 'NM_AUDITOR='||obter_nome_usuario(nm_usuario_auditor_w) ),
				nm_usuario_p	=> nm_usuario_p,
				ie_commit_p	=> 'N');

commit;

END;

$body$
